WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('2000-05-25' AS DATE) AND CAST('2000-07-24' AS DATE)), filtered_item AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 62 AND 62 + 30 AND i_manufact_id IN (129, 270, 821, 423)), filtered_inventory AS (SELECT inv_item_sk, inv_date_sk FROM inventory WHERE inv_quantity_on_hand BETWEEN 100 AND 500), qualified_inventory AS (SELECT inv_item_sk FROM filtered_inventory AS inv JOIN filtered_date AS d ON inv.inv_date_sk = d.d_date_sk JOIN filtered_item AS i ON inv.inv_item_sk = i.i_item_sk)
SELECT i_item_id, i_item_desc, i_current_price FROM filtered_item AS i WHERE EXISTS(SELECT 1 FROM qualified_inventory AS qi WHERE qi.inv_item_sk = i.i_item_sk) AND EXISTS(SELECT 1 FROM store_sales AS ss WHERE ss_item_sk = i.i_item_sk) ORDER BY i_item_id LIMIT 100