WITH date_filter AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999), store_filter AS (SELECT s_store_sk, s_store_name, s_company_name FROM store), item_branch1 AS (SELECT i_item_sk, i_category, i_class, i_brand FROM item WHERE i_category IN ('Books', 'Electronics', 'Sports') AND i_class IN ('computers', 'stereo', 'football')), item_branch2 AS (SELECT i_item_sk, i_category, i_class, i_brand FROM item WHERE i_category IN ('Men', 'Jewelry', 'Women') AND i_class IN ('shirts', 'birdal', 'dresses')), branch1_sales AS (SELECT i_category, i_class, i_brand, s_store_name, s_company_name, d_moy, SUM(ss_sales_price) AS sum_sales FROM store_sales JOIN date_filter ON ss_sold_date_sk = d_date_sk JOIN item_branch1 ON ss_item_sk = i_item_sk JOIN store_filter ON ss_store_sk = s_store_sk GROUP BY i_category, i_class, i_brand, s_store_name, s_company_name, d_moy), branch2_sales AS (SELECT i_category, i_class, i_brand, s_store_name, s_company_name, d_moy, SUM(ss_sales_price) AS sum_sales FROM store_sales JOIN date_filter ON ss_sold_date_sk = d_date_sk JOIN item_branch2 ON ss_item_sk = i_item_sk JOIN store_filter ON ss_store_sk = s_store_sk GROUP BY i_category, i_class, i_brand, s_store_name, s_company_name, d_moy), combined_sales AS (SELECT * FROM branch1_sales UNION ALL SELECT * FROM branch2_sales)
SELECT * FROM (SELECT i_category, i_class, i_brand, s_store_name, s_company_name, d_moy, sum_sales, AVG(sum_sales) OVER (PARTITION BY i_category, i_brand, s_store_name, s_company_name) AS avg_monthly_sales FROM combined_sales) AS tmp1 WHERE CASE WHEN (avg_monthly_sales <> 0) THEN (ABS(sum_sales - avg_monthly_sales) / avg_monthly_sales) ELSE NULL END > 0.1 ORDER BY sum_sales - avg_monthly_sales, s_store_name, 1, 2, 3, 5, 6, 7, 8 LIMIT 100