WITH date_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 11), address_filtered AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -7), hd_filtered AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential LIKE 'Unknown%'), branch_m_unknown AS (SELECT cc_call_center_id, cc_name, cc_manager, 'M' AS cd_marital_status, 'Unknown' AS cd_education_status, SUM(cr_net_loss) AS Returns_Loss FROM call_center JOIN catalog_returns ON cr_call_center_sk = cc_call_center_sk JOIN date_filtered ON cr_returned_date_sk = d_date_sk JOIN customer ON cr_returning_customer_sk = c_customer_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk JOIN address_filtered ON ca_address_sk = c_current_addr_sk JOIN hd_filtered ON hd_demo_sk = c_current_hdemo_sk WHERE cd_marital_status = 'M' AND cd_education_status = 'Unknown' GROUP BY cc_call_center_id, cc_name, cc_manager), branch_w_advanced AS (SELECT cc_call_center_id, cc_name, cc_manager, 'W' AS cd_marital_status, 'Advanced Degree' AS cd_education_status, SUM(cr_net_loss) AS Returns_Loss FROM call_center JOIN catalog_returns ON cr_call_center_sk = cc_call_center_sk JOIN date_filtered ON cr_returned_date_sk = d_date_sk JOIN customer ON cr_returning_customer_sk = c_customer_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk JOIN address_filtered ON ca_address_sk = c_current_addr_sk JOIN hd_filtered ON hd_demo_sk = c_current_hdemo_sk WHERE cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree' GROUP BY cc_call_center_id, cc_name, cc_manager)
SELECT Call_Center, Call_Center_Name, Manager, Returns_Loss FROM (SELECT * FROM branch_m_unknown UNION ALL SELECT * FROM branch_w_advanced) AS combined ORDER BY Returns_Loss DESC