WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 11), filtered_customer_demographics AS (SELECT cd_demo_sk FROM customer_demographics WHERE (cd_marital_status = 'M' AND cd_education_status = 'Unknown') OR (cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree')), filtered_household_demographics AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential LIKE 'Unknown%'), filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -7), filtered_catalog_returns AS (SELECT cr_call_center_sk, cr_net_loss, cr_returning_customer_sk FROM catalog_returns AS cr JOIN filtered_dates AS fd ON cr.cr_returned_date_sk = fd.d_date_sk)
SELECT cc.cc_call_center_id AS Call_Center, cc.cc_name AS Call_Center_Name, cc.cc_manager AS Manager, SUM(fcr.cr_net_loss) AS Returns_Loss FROM call_center AS cc JOIN filtered_catalog_returns AS fcr ON cc.cc_call_center_sk = fcr.cr_call_center_sk JOIN customer AS c ON fcr.cr_returning_customer_sk = c.c_customer_sk JOIN filtered_customer_demographics AS cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN filtered_household_demographics AS hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN filtered_customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk GROUP BY cc.cc_call_center_id, cc.cc_name, cc.cc_manager ORDER BY SUM(fcr.cr_net_loss) DESC