WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2000-01-27' AND CAST('2000-04-26' AS DATE)), item_manufacturer_filtered AS (SELECT i_item_sk FROM item WHERE i_manufact_id = 350), web_sales_with_dates AS (SELECT ws_item_sk, ws_ext_discount_amt FROM web_sales JOIN filtered_dates ON ws_sold_date_sk = d_date_sk), item_avg_discount AS (SELECT ws_item_sk, 1.3 * AVG(ws_ext_discount_amt) AS avg_threshold FROM web_sales_with_dates GROUP BY ws_item_sk)
SELECT SUM(ws.ws_ext_discount_amt) AS "Excess Discount Amount" FROM web_sales_with_dates AS ws JOIN item_manufacturer_filtered AS i ON ws.ws_item_sk = i.i_item_sk JOIN item_avg_discount AS avg ON ws.ws_item_sk = avg.ws_item_sk WHERE ws.ws_ext_discount_amt > avg.avg_threshold ORDER BY SUM(ws.ws_ext_discount_amt) LIMIT 100