WITH date_range AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '1999-02-01' AND CAST('1999-04-02' AS DATE)), filtered_ca AS (SELECT ca_address_sk FROM customer_address WHERE ca_state = 'IL'), filtered_web_site AS (SELECT web_site_sk FROM web_site WHERE web_company_name = 'pri'), multi_warehouse_orders AS (SELECT ws_order_number FROM web_sales GROUP BY ws_order_number HAVING COUNT(DISTINCT ws_warehouse_sk) > 1), returned_orders AS (SELECT DISTINCT wr_order_number FROM web_returns), filtered_ws AS (SELECT ws_order_number, ws_ext_ship_cost, ws_net_profit FROM web_sales AS ws1 JOIN date_range AS dr ON ws1.ws_ship_date_sk = dr.d_date_sk JOIN filtered_ca AS ca ON ws1.ws_ship_addr_sk = ca.ca_address_sk JOIN filtered_web_site AS ws ON ws1.ws_web_site_sk = ws.web_site_sk)
SELECT COUNT(DISTINCT fws.ws_order_number) AS "order count", SUM(fws.ws_ext_ship_cost) AS "total shipping cost", SUM(fws.ws_net_profit) AS "total net profit" FROM filtered_ws AS fws JOIN multi_warehouse_orders AS mwo ON fws.ws_order_number = mwo.ws_order_number LEFT JOIN returned_orders AS ro ON fws.ws_order_number = ro.wr_order_number WHERE ro.wr_order_number IS NULL ORDER BY COUNT(DISTINCT fws.ws_order_number) LIMIT 100