WITH filtered_items AS (SELECT i_item_sk FROM item WHERE i_manufact_id = 320), filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2002-02-26' AND CAST('2002-02-26' AS DATE) + INTERVAL '90 DAY') SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM web_sales AS ws INNER JOIN filtered_items AS i ON i.i_item_sk = ws.ws_item_sk INNER JOIN filtered_dates AS d ON d.d_date_sk = ws.ws_sold_date_sk WHERE ws.ws_ext_discount_amt > (SELECT 1.3 * AVG(ws_ext_discount_amt) FROM web_sales, date_dim WHERE ws_item_sk = i_item_sk AND d_date BETWEEN '2002-02-26' AND (CAST('2002-02-26' AS DATE) + INTERVAL '90 DAY') AND d_date_sk = ws_sold_date_sk) ORDER BY SUM(ws_ext_discount_amt) LIMIT 100;