WITH common_scan AS (SELECT ws_item_sk, ws_ext_discount_amt FROM web_sales INNER JOIN date_dim ON d_date_sk = ws_sold_date_sk WHERE d_date BETWEEN '2002-02-26' AND CAST('2002-02-26' AS DATE) + INTERVAL '90 DAY'), thresholds AS (SELECT ws_item_sk, 1.3 * AVG(ws_ext_discount_amt) AS threshold FROM common_scan GROUP BY ws_item_sk) SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM common_scan AS cs INNER JOIN item ON i_item_sk = cs.ws_item_sk INNER JOIN thresholds AS t ON t.ws_item_sk = cs.ws_item_sk WHERE i_manufact_id = 320 AND cs.ws_ext_discount_amt > t.threshold ORDER BY SUM(ws_ext_discount_amt) LIMIT 100;